import React, { useState } from 'react'

import styles from './styles.scss'

const Arr = [
    1, 2, 3
]

const App = () => {

    const [text, setText] = useState('Введи текст')
    const [data, setData] = useState([])
    const [number, setNumber] = useState()



    function onChangeInput(event) {
        setText(event.target.value)
    }

    function onClick1 (){
        setData([...data, text])
    }


    function onClick2 (eventClick){
        let index = Number(eventClick.target.getAttribute('i'))
        let newData = data.filter((ddd, indexArray) => {
            if(indexArray === index) {
                return false
            } else return true
        })
        setData(newData)
    }

    function OnClickButton1(){
        let number = Arr[Math.floor(Math.random() * Arr.length)]
        setNumber(number)
    }

    function OnClickCheck1(){
        if(number === 1) {
            return true
        }
    }

    function OnClickCheck2(){
        if(number === 2) {
            return true
        }
    }

    function OnClickCheck3(){
        if(number === 3) {
            return console.log(3)
        }
    }

    return(
        <div className="container">
            <div class="name">
                <p>{text}</p>
                <input value={text} onChange={onChangeInput}></input>
                <button onClick={onClick1}>Добавить задачу</button>
            </div>
            <div className="list">
                {data.map((el, i )=> (<div>{el} <button i={i} onClick={onClick2}>Удалить</button></div>))}
            </div>

            <div class="body">
                <div class="wrap">{number}</div>
                <button onClick={OnClickButton1}>выведи число</button>
                <button onClick={OnClickCheck1}>Один</button>
                <button onClick={OnClickCheck2}> Два</button>
                <button onClick={OnClickCheck3}>Три</button>
            </div>


        </div>
    )
}

export default App
