import React, {useState} from 'react'
//область видимости функций
//onClick везде и при нажатии. onChange на инпутах и при изменении


const App = () => {
    const [step, setStep] = useState(0);
    function onclick4 () {
        setStep(step +1)
    }
    function onclick5 () {
        setStep(step -1)
    }


    const [count, setCount] = useState(0);
    function onclick1 () {
        setCount(count + step)
    }
    function onclick2 () {
        setCount(count - step)
    }
    function onclick3 () {
        setCount(0)
    }

return(
    <div className="container">
        <div className="www">{step}</div>
        <button onClick={onclick4}>-</button>
        <button onClick={onclick5}>+</button>

        <div className="wrap">{count}</div>
        <button onClick={onclick2}>-</button>
        <button onClick={onclick3}>ref</button>
        <button onClick={onclick1}>+</button>
    </div>
    )
}

export default App
