import React, {useEffect, useState} from 'react'



const App = () => {
const [time, setTime] = useState(0)

useEffect(()=>{
    console.log('test')
}, [time]) 
//setInterval(()=>{setTime(time + 1)},1000)
// console.log(time)

function onclick3 () {
    setTime(time + 1)
}

return(
    <div className="container">
        <div className="wrap">{time}</div>
        <button onClick={onclick3}>+</button>
    </div>
    )
}

export default App
