import React, { useState } from 'react'

const ListItem = ({ text, multiplier }) => {
    const [count, setCount] = useState(+text);

    return (
        <div className="item">
            <button onClick={() => setCount(count - 1)}>-</button>
            {count * multiplier}
            {' '}
            { count > 3 && <b>Ok</b> }
            <button onClick={() => setCount(count + 1)}>+</button>
        </div>
    )
}

export default ListItem
