import React from 'react'

const Header = () => {
    return (
        <div>
            <h1>ННН</h1>
            <h2>2000 очков</h2>
        </div>
    )
}

const SerchPanel = () => {
    return (
        <input type="text" placeholder="поиск среди игроков"/>
    )
}

const PostAddForm = () => {
    return (
       <form>
           <input type='text' placeholder="как тебя зовут">
           </input>
           <button type="submit">
                В список
           </button>
       </form>
    )
}


const PostListItem = ({label, liked = false}) => {

    let classNames = 'form';
    if (liked) {
        classNames += ' important';
    }

    return (
        <li className={classNames}>
            <span>
                {label}
            </span>
            <div>
                <button>
                    like
                </button>
                <button>
                    dislike
                </button>
            </div>
        </li>
    )
}

const PostList = ({props}) => {
    return (
       <ul>
           <PostListItem label='nikta' liked/>
           <PostListItem label='max'/>
           <PostListItem label='anton'/>
       </ul>
    )
}

const PostStatusFilter = () => {
    return (
        <div>
            <button type="button">
         all
            </button>
            <button type="button">
                like
                </button>
        </div>
    )
}

export const NewReg = () => {

    const data = [
        {label:'nikta', liked: true},
        {label:'anton', liked: false},
        {label:'max', liked: true},
    ];

    return (
        <>
        <h1>Hello</h1>
        <Header/>
        <SerchPanel/>
        <PostStatusFilter/>
        <PostList/>
        <PostAddForm/>
        </>)
}
