import React, { useState} from 'react'

const items = [
    { 
        name: 'paper', 
        img: '/paper-item.png',
        id: 1
    },
    { 
        name: 'metall', 
        img: '/metall-can.jpg',
        id: 2 
    },
    { 
        name: 'regular', 
        img: '/apple.png',
        id: 3
    },
]

export const ItemGeneration = () => {
    const [item, setItem] = useState()

    const CreateItem = () => {
        let number = items.img[Math.floor(Math.random() * items.length)]
        setItem(number)
    }
    return (
        <div class="item">
            {CreateItem}
        </div>
    )
}
