import React, {Component} from 'react'

import  './style.css'

export default class PostListItem extends Component {

    render() {
        const {label, onDelete, onToggleImportanr, onLiked} = this.props;
        const {important, like} = this.state;
        let classNames = 'item';
        if (important) {
            classNames += ' important'
        }

        if (like) {
            console.log('Лайк бля')
        }
        return (
            <div className={classNames}>
                <span onClick={this.onLike}>
                    {label}
                </span>
                <div >
                    <button onClick={this.onImportnant}>
                        <i>Star</i>
                    </button>
                    <button onClick={onDelete}>
                        <i>Trash</i>
                    </button>
                    <i>Heart</i>
                </div>
            </div>
        )
    }
}
