import React, {Component} from 'react'

import  './style.css'

export default class PostListItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            important: false,
            like: false
        };
        this.onImportnant = this.onImportnant.bind(this);
        this.onLike = this.onLike.bind(this);
    }

    onImportnant() {
        this.setState(({important})=> ({
            important: !important
        }))
    }

    onLike() {
        this.setState(({like})=> ({
            like: !like
        }))
    }
    render() {
        const {label} = this.props;
        const {important, like} = this.state;
        let classNames = 'item';
        if (important) {
            classNames += ' important'
        }

        if (like) {
            classNames += ' like'
        }
        return (
            <div className={classNames}>
                <span onClick={this.onLike}>
                    {label}
                </span>
                <div >
                    <button onClick={this.onImportnant}>
                        <i>Star</i>
                    </button>
                    <button>
                        <i>Trash</i>
                    </button>
                    <i>Heart</i>
                </div>
            </div>
        )
    }
}
