import React, {useState} from 'react'

const App = () => {
    const [number, setNumber] = useState(10)
    function onClick1 () {
        setNumber(number*3.14)
    }
    function onClick2 () {
        setNumber(number-9)
    }
    return(
        <div className="container">
            <div className="wrap">{number}</div>
            <button onClick={onClick1}>умножить на 3.14</button>
            <button onClick={onClick2}>вычесть 9</button>
        </div>
    )

}

export default App
