import React, {Component} from 'react'

import  './style.css'

export default class PostListItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            important: false
        };
    }

    onImportnant() {
        this.setState(({important})=> ({
            important: !important
        }))
    }
    render() {
        const {label, important = false} = this.props;
        let classNames = 'item';
        if (important) {
            classNames += ' important'
        }
        return (
            <div className={classNames}>
                <span>
                    {label}
                </span>
                <div >
                    <button onClick={this.onImportnant}>
                        <i>Star</i>
                    </button>
                    <button>
                        <i>Trash</i>
                    </button>
                    <i>Heart</i>
                </div>
            </div>
        )
    }
}
