import React, { useState} from 'react'





const bucket_data = [
    {img: "/metall.jpg"},
    {img:"/paper.jpg"},
    {img:"/plactic.jpg"}
];


const Arr = [
    1, 2, 3
]
let counter = 0
let errors = 0

export const Game = () => {
    const [number, setNumber] = useState()
    const [status, setStatus] = useState()
    const [isStarted, setIsStarted] = useState()

    function OnClickButton1(){
        let number = Arr[Math.floor(Math.random() * Arr.length)]
        setNumber(number)
        setStatus('')
    }
    function OnClickCheck1(){
        if(number === 1) {
            setStatus(true)
            counter++;
        }   else {
            setStatus(false)
            errors++;
        }
        return (
            <img src={bucket_data[0].img} ></img>
        )
    }
    function OnClickCheck2(){
        if(number === 2) {
            setStatus(true)
            counter++;
        }   else {
            setStatus(false)
            errors++;
        }
    }
    function OnClickCheck3(){
        if(number === 3) {
            setStatus(true)
            counter++;
        }   else {
            setStatus(false)
            errors++;
        }
    }
    function GameStart() {
        if(isStarted === true) {
            return
        }
        setIsStarted(true)
        setInterval(OnClickButton1, 3000);
    }



    return(
        <div class="game">
            <div class="body">
                <div class="wrap">{number}</div>
                <button onClick={GameStart}>Начать игру</button>
                <button onClick={OnClickCheck1}>Один</button>
                <button onClick={OnClickCheck2}> Два</button>
                <button onClick={OnClickCheck3}>Три</button>
            </div>
            {status && (
                <div class="prav">Правильно</div>
            )}
            {!status && (
                <div class="error">Ошибка</div>
            )}
            <div class="count">Счетчик очков:{counter}</div>
            <div class="count">Ошибки:{errors}</div>
            <div>
                <p>Мусораный контейнер</p>
                {/* {OnClickCheck1===true && <img src={bucket_data[0].img} ></img>} */}
                {OnClickCheck2===true && <img src={bucket_data[1].img} ></img>}
                {OnClickCheck3===true && <img src={bucket_data[2].img} ></img>}
            </div>
        </div>
    )
}

export default Game
