import React, { useState, useEffect } from 'react'

import styles from './styles.scss'

const Arr = [
    1, 2, 3
]

let counter = 0
let errors = 0

const App = () => {
    const [text, setText] = useState('')
    const [data, setData] = useState([])
    const [number, setNumber] = useState()
    const [status, setStatus] = useState()




    function onChangeInput(event) {
        setText(event.target.value)
    }

    function onClick1 (){
        setData([...data, text])
    }


    function onClick2 (eventClick){
        let index = Number(eventClick.target.getAttribute('i'))
        let newData = data.filter((ddd, indexArray) => {
            if(indexArray === index) {
                return false
            } else return true
        })
        setData(newData)
    }


    // let OnClickTimeOut = setTimeout(function OnClickButton1(){
    //     let number = Arr[Math.floor(Math.random() * Arr.length)];
    //     setStatus('');
    //     setNumber(number);
    //     setTimeout(OnClickButton1, 20000)
    // })

    let OnClickTimeOut = setInterval(OnClickButton1, 1000);



    function OnClickButton1(){
        let number = Arr[Math.floor(Math.random() * Arr.length)]
        setNumber(number)
        setStatus('')
    }



    function OnClickCheck1(){
        if(number === 1) {
            setStatus(true)
            counter++;
        }   else {
            setStatus(false)
            errors++;
        }
    }

    function OnClickCheck2(){
        if(number === 2) {
            setStatus(true)
            counter++;
        }   else {
            setStatus(false)
            errors++;
        }
    }

    function OnClickCheck3(){
        if(number === 3) {
            setStatus(true)
            counter++;
        }   else {
            setStatus(false)
            errors++;
        }
    }



    return(
        <div className="container">
            <div class="name">

                <input value={text} onChange={onChangeInput}></input>
                <button onClick={onClick1}>Введи ник и нажми</button>
            </div>
            <div className="list">
                {data.map((el, i )=> (<div>{el} <button i={i} onClick={onClick2}>Удалить</button></div>))}
            </div>

            <div class="body">
                <div class="wrap">{number}</div>
                <button onClick={OnClickTimeOut}>выведи число</button>
                <button onClick={OnClickCheck1}>Один</button>
                <button onClick={OnClickCheck2}> Два</button>
                <button onClick={OnClickCheck3}>Три</button>
            </div>

            {status && (
                <div class="prav">Правильно</div>
            )}
            {!status && (
                <div class="error">Ошибка</div>
            )}

            <div class="count">Счетчик очков:{counter}</div>
            <div class="count">Ошибки:{errors}</div>
        </div>
    )
}

export default App
