import React from 'react'

import  './style.css'

const PostListItem = ({label, important = false}) => {

    let classNames = 'item';
    if (important = true) {
        classNames += ' important'
    }

    return (
        <li className={classNames}>
            <span>
                {label}
            </span>
            <div className="item">
                <button>
                    <i>Star</i>
                </button>
                <button>
                    <i>Trash</i>
                </button>
                <i>Heart</i>
            </div>
        </li>
    )
}

export default PostListItem
