import React, { useState } from 'react'

const App = () => {
    const [text, setText] = useState('Введи текст')

    const [data, setData] = useState([])

    function onChangeInput(event) {
        setText(event.target.value)
    }

    function onClick1 (){
        setData([...data, text])
    }

    function onClick2 (eventClick){
        console.log(eventClick.target)
    }
    return(
        <div className="container">
            <div className="wrap">
                <p>{text}</p>
                <input value={text} onChange={onChangeInput}></input>
                <button onClick={onClick1}>Добавить задачу</button>
            </div>
            <div className="list">
                {data.map((el, i )=> (<div>{el} <button i={i} onClick={onClick2}>Удалить</button></div>))}

            </div>
        </div>
    )

}

export default App
