import React from 'react'

class PostListItem extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            like: false
        };
        this.onLike = this.onLike.bind(this);
    }

    onLike() {
        this.setState(({liked})=>({
            liked: !liked
        }))
    }
    render () {
        const {label, liked = false} = this.props
        let classNames = 'form';
        if (liked) {
            classNames += ' important';
        }
        return (
            <li className={classNames}>
                <span>
                    {label}
                </span>
                <div>
                    <button onClick={this.onLike}>
                        like
                    </button>
                    <button>
                        dislike
                    </button>
                </div>
            </li>
        )
    }
}


const Header = () => {
    return (
        <div>
            <h1>ННН</h1>
            <h2>2000 очков</h2>
        </div>
    )
}

const SerchPanel = () => {
    return (
        <input type="text" placeholder="поиск среди игроков"/>
    )
}

const PostAddForm = () => {
    return (
       <form>
           <input type='text' placeholder="как тебя зовут">
           </input>
           <button type="submit">
                В список
           </button>
       </form>
    )
}



const PostList = ({posts}) => {

    const element = posts.map((item)=>{
        const {id, ...itemProps} = item
        return (
            <li  key={id}>
                <PostListItem
                    {...itemProps}>
                </PostListItem>
            </li>
        )
    });

    return (
        <ul>{element}</ul>

    )
}

const PostStatusFilter = () => {
    return (
        <div>
            <button type="button">
         all
            </button>
            <button type="button">
                like
                </button>
        </div>
    )
}

export const NewReg = () => {

    const data = [
        {label:'nikta', liked: true, id: 'ddd'},
        {label:'anton', liked: false,  id: 'erewrwer'},
        {label:'max', liked: true,  id: 'vdfv'}
    ];

    return (
        <>
        <h1>Hello</h1>
        <Header/>
        <SerchPanel/>
        <PostStatusFilter/>
        <PostList posts={data}/>
        <PostAddForm/>
        </>)
}
