import React, {useState} from 'react'
//область видимости функций


const App = () => {
    const [count, setCount] = useState(0);
    function onclick1 () {
        setCount(count +1)
    }

return(
    <div className="container">
        <div className="wrap">{count}</div>
        <button>-</button>
        <button>ref</button>
        <button onClick={onclick1}>+</button>
    </div>
    )
}

export default App
