import React, {useState} from 'react'

const App = () => {
    const [text, setText] = useState('Введи текст')
    function onChangeInput(event) {
        setText(event.target.value)
    }
    const [item, setItem] = useState()
    function onClick1 (text){
        setItem(text)
    }
    return(
        <div className="container">
            <div className="wrap">
                <p>{text}</p>
                <input value={text} onChange={onChangeInput}></input>
                <button onClick={onClick1}>Добавить задачу</button>
            </div>
            <div className="list">
                <li>{item}</li>
            </div>
        </div>
    )

}

export default App
