    constructor(props) {
        super(props);
        this.state = {
            data : [
                {label:'nikta', liked: true, id: 'ddd'},
                {label:'anton', liked: false,  id: 'erewrwer'},
                {label:'max', liked: true,  id: 'vdfv'}
            ]
        }
    }
